import React from 'react';
import {Button} from './button.components';
import {Link} from 'react-router-dom';
import './hero-section.styles.css';

function HeroSection({
    lightBg,
    topLine,
    lightText,
    lightTextDesc,
    headline,
    description,
    buttonLabel,
    img,
    alt,
    imgStart
}) {
    return (
        <div className={lightBg ? 'home__hero-section' : 'home__hero-section darkBg'}>
            <div className='container'>
            <div className='row home__hero-row'
            style={{
                display: 'flex', 
                flexDirection: imgStart === 'start' ? 'row-reverse' : 'row'
            }}
            >
            <div className='col'>
                <div className='home__hero-text-wrapper'>
                <div className='top-line'>{topLine}</div>
                <h1 className={lightText ? 'heading' : 'heading dark'}>{headline}</h1>
                <p className={lightTextDesc ? 'home__hero-subtitle' : 'home__hero-subtitle dark'}>
                    {description}</p>
                    <Link to='/sign-up'>
                        <Button buttonSize='btn--wide' buttonColor='blue'>{buttonLabel}</Button>
                    </Link>
                </div>
            </div>
                <div className='col'>
                <div className='home__hero-img-wrapper'>
                    <img src={img} alt={alt} className='home__hero-img'/>
                </div>
                </div>
            </div>
            </div>
        </div>
    )
};

export default HeroSection;
//we add {} properties were gnna pass into compoennt so we cna copy and paste it in other areas
//if light background is true, make background white if not dark background
//row reverse switches position of the image
//if lighttext is true we have class heading else heading dark
